        private static Dictionary<string, string[]> pinyinDic = new Dictionary<string, string[]>();
        static PinYin()
        {
            string[] code = codes.Split('|');
            foreach (string c in code)
            {
                string[] cs = c.Split(':');
                string character = cs[0];
                string[] pinyins = cs[1].Split(',');
                pinyinDic.Add(character, pinyins);
            }
        }
        /// <summary> 通过字符获取拼音集合
        /// </summary>
        /// <param name="character">字符</param>
        /// <returns>返回拼音数组，不存在则返回null</returns>
        public static string[] GetPinYin(char character)
        {
            if (pinyinDic.ContainsKey(character.ToString()))
            {
                return pinyinDic[character.ToString()];
            }
            else { return null; }
        }
        //āáǎàōóǒòēéěèīíǐìūúǔùǖǘǚǜü
        /// <summary>
        /// 判断输入是否全为中文  
        /// </summary>
        /// <param name="content"></param>
        /// <returns></returns>
        public static bool IsAllChinese(string content)
        {
            if (Regex.IsMatch(content, @"^[\u4e00-\u9fa5]*$"))
            {
                return true;
            }
            else
            {
                return false;
            }
        }
        /// <summary>
        /// 判断是否全部英语
        /// </summary>
        /// <param name="content"></param>
        /// <returns></returns>
        public static bool IsAllEnglish(string content)
        {
            if (Regex.IsMatch(content, @"^[A-Za-z]*$"))
            {
                return true;
            }
            else
            {
                return false;
            }
        }
        /// <summary>
        /// 判断输入是否为中文  
        /// </summary>
        /// <param name="content"></param>
        /// <returns></returns>
        public static bool HasChinese(string content)
        {
            if (Regex.IsMatch(content, @"[\u4e00-\u9fa5]"))
            {
                return true;
            }
            else
            {
                return false;
            }
        }
        /// <summary>
        /// 获取指定字符串的中文拼音
        /// </summary>
        /// <returns>中文拼音</returns>
        /// <param name="target">目标字符串</param>
        public static string GetChinesePinYin(string target)
        {
            StringBuilder chinesePinYin = new StringBuilder();
            for (int i = 0; i < target.Length;i++)
            {
                chinesePinYin.Append(GetChinesePinYinResult(target[i])); 
            }
            return chinesePinYin.ToString();
        }
        /// <summary>
        /// 过滤不是中文的字符
        /// </summary>
        /// <returns>中文拼音</returns>
        /// <param name="str">目标字符串</param>
        private static string GetChinesePinYinResult(char str)
        {
            if(HasChinese(str.ToString()))
            {
                return GetPinYin(str)[0]+" ";
            }
            else
            {
                switch (str)
                {
                    case '；':
                        return ';'.ToString();
                    case '，':
                        return ','.ToString();
                    case '！':
                        return '!'.ToString();
                    default:
                        return str.ToString();
                }
            }
        }
        /// <summary>
        /// 外部文本转化成拼音时调用
        /// </summary>
        /// <returns>转化完成的文本</returns>
        /// <param name="target">需要转化的文本</param>
        public static string GetShowText(string target)
        {
            string result = null;
			//LogManager.Log("原话",target);
            target = MakeStr(target);
			//LogManager.Log("过滤" , target);
            if (LanguageManager.languageType == LanguageType.Chinese)
            {
                result = target;
            }
            else
            {
                if(isShowPinYin)
                {
                    result = GetChinesePinYin(target);
                }
                else
                {
                    result = target;
                }
            }
			//LogManager.Log("拼音" , result);
            return result;
        }
        /// <summary>
        /// 用途：当中文转换成拼音时，如果存在多音字或者中外姓名时必须使用此方法进行过滤人名或者多音字之后再翻译成拼音。
        /// 用法：策划配表格式 "嗨{hāi},我的{de}名字叫[汪](继续).他的名字叫(詹姆斯).
        /// 规则：{多音字},[姓],(名)。
        /// </summary>
        /// <returns>过滤之后的文本</returns>
        /// <param name="str">过滤之前的文本</param>
        public static string MakeStr(string str)
        {
            StringBuilder resultStr = new StringBuilder();
            StringBuilder pinYinStr = new StringBuilder();
            for (int i = 0; i < str.Length; i++)
            {
                if(isShowPinYin)
                {
                    switch (str[i])
                    {
                        case '{':
                            while (str[++i] != '}')
                            {
                                //存取多音字的拼音
                                pinYinStr.Append(str[i]);
                            }
                            break;
                        case '[':
                            while (str[++i] != ']')
                            {
                                //存取名字的姓（首字母大写）
                                string nameStr = GetPinYin(str[i])[0];
                                for (int j = 0; j < nameStr.Length; j++)
                                {
                                    if (j == 0)
                                    {
                                        pinYinStr.Append(nameStr[j].ToString().ToUpper());
                                    }
                                    else
                                    {
                                        pinYinStr.Append(nameStr[j]);
                                    }
                                }
                            }
                            break;
                        case '(':
                            bool isOne = true;
                            while (str[++i] != ')')
                            {
                                //存取名字的名（首字母大写两个名之间没有空格）
                                string nameStr = GetPinYin(str[i])[0];
                                for (int j = 0; j < nameStr.Length; j++)
                                {
                                    if (j == 0)
                                    {
                                        if (isOne)
                                        {
                                            isOne = false;
                                            pinYinStr.Append(nameStr[j].ToString().ToUpper());
                                        }
                                        else
                                        {
                                            pinYinStr.Append(nameStr[j]);
                                        }
                                    }
                                    else
                                    {
                                        pinYinStr.Append(nameStr[j]);
                                    }
                                }
                            }
                            break;
                        default:
                            //当不是姓或名和多音字时原样输出
                            switch (str[i])
                            {
                                case '；':
                                    resultStr.Append(';');
                                    break;
                                case '，':
                                    resultStr.Append(',');
                                    break;
                                case '！':
                                    resultStr.Append('!');
                                    break;
                                default:
                                    resultStr.Append(str[i]);
                                    break;
                            }
                            break;
                    }
                }
                else
                {
                    if(str[i]!='{'&&str[i] !='}'&&str[i]!='('&&str[i] !=')'&&str[i] !='['&&str[i] !=']')
                    {
                        resultStr.Append(str[i]);
                    }
                }
                if (pinYinStr.Length != 0)
                {
                    resultStr.Append(pinYinStr + " ");
                    pinYinStr.Remove(0, pinYinStr.Length);
                }
            }
            return resultStr.ToString();
        }
    }
}
